CREATE TABLE [DateDim] (
  [DateID] Integer NOT NULL,
  [Date] Date
);


CREATE TABLE [RegionDim] (
  [RegionID] Integer NOT NULL,
  [RegionName] Varchar(255)
);

CREATE TABLE [WeatherMetricsFact] (
  [DateID] Integer NOT NULL,
  [RegionID] Integer NOT NULL,
  [AverageTemperature] Integer,
  [Humidity] Integer,
  [WindSpeed] Integer,
  [Rainfall] Integer,
  [SunshineHours] Integer
);

SELECT * FROM [RegionDim]
SELECT * FROM [DateDim]
SELECT * FROM [WeatherMetricsFact] 

Drop TABLE [RegionDim]



ALTER TABLE DateDim add CONSTRAINT PK_DateDim PRIMARY KEY NONCLUSTERED (DateID) NOT ENFORCED;
ALTER TABLE RegionDim add CONSTRAINT PK_RegionDim PRIMARY KEY NONCLUSTERED (RegionID) NOT ENFORCED;
ALTER TABLE WeatherMetricsFact add CONSTRAINT PK_WeatherMetricsFact PRIMARY KEY NONCLUSTERED (DateID) NOT ENFORCED;
ALTER TABLE WeatherMetricsFact add CONSTRAINT PK_WeatherMetricsFact PRIMARY KEY NONCLUSTERED (RegionID) NOT ENFORCED;
